import { Override, Data } from "framer";

export const data = Data({
  hueRotate: 0,
});

export const HueValue: Override = props => {
  return {
    value: data.hueRotate,
  };
};

export const HueSlider: Override = props => {
  return {
    onValueChange: (value: number) => {
      data.hueRotate = value;
    },
  };
};
