import { Data, animate, Override, Animatable } from "framer";

const fbBlue: number = "#385898";
const fbBluePressed: number = "#314d86";

const data = Data({
  scale: Animatable(1),
  backgroundColor: Animatable(fbBlue)
});

export const FBLoginButton: Override = () => {
  return {
    scale: data.scale,
    background: data.backgroundColor,
    onTapStart() {
      animate.bezier(data.backgroundColor, fbBluePressed, {
        curve: [0.5, 0, 1, 1],
        duration: 0.1
      });
      animate.bezier(data.scale, 0.99, {
        curve: [0.5, -1, 1, 1],
        duration: 0.1
      });
    },
    onTapEnd() {
      animate.bezier(data.backgroundColor, fbBlue, {
        curve: [0.5, 0, 1, 1],
        duration: 0.1
      });
      animate.bezier(data.scale, 1, {
        curve: [0.5, -1, 1, 1],
        duration: 0.1
      });
    }
  };
};
