import * as React from "react";
import { Frame, PropertyControls, ControlType } from "framer";
import TweetEmbed from "react-tweet-embed";

// Define type of property
interface Props {
  width: number;
  height: number;
  tweetid: string;
}

export class TweetEmbed extends React.Component<Partial<Props>> {
  // Set default properties
  static defaultProps = {
    width: 400,
    height: 400,
    tweetid: "463440424141459456",
  };

  // Items shown in property panel
  static propertyControls: PropertyControls = {
    tweetid: { type: ControlType.String, title: "Tweet" },
  };

  render() {
    var tweetUrl = this.props.tweetid.split("/");
    var tweetID = tweetUrl[tweetUrl.length - 1];
    return <TweetEmbed id={tweetID} />;
  }
}
